    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        dispose();
        Deposit dep = new Deposit();
        a.setUserName(jTextField1.getText());
        a.setCkBal(100.0);
        a.setSavBal(100.0);
        String name = a.getUserName();
        double cBal = a.getCkBal();
        double sBal = a.getSavBal();
        Account account = new Account(name, cBal, sBal);
        AccountDB.saveAccount(account);
        dep.jLabel4.setText(jTextField1.getText());
        dep.setVisible(true);
    }//GEN-LAST:event_jButton1ActionPerformed

