    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        dispose();
        Balance bal = new Balance();
        
        bal.jLabel5.setText(jTextField1.getText());
        a.setUserName(jTextField1.getText());
        a.setCkBal(100.0);
        a.setSavBal(100.0);
        String name = a.getUserName();
        double cBal = a.getCkBal();
        double sBal = a.getSavBal();
        Account account = new Account(name, cBal, sBal);
        AccountDB.saveAccount(account);
        NumberFormat currency = NumberFormat.getCurrencyInstance();
        String ckBal = currency.format(cBal);
        String savBal = currency.format(sBal);
        bal.jLabel6.setText(ckBal);
        bal.jLabel7.setText(savBal);
        bal.setVisible(true);
    }//GEN-LAST:event_jButton3ActionPerformed

